import { hexColorToRGB } from "@unlimited-react-components/kernel";
import * as React from "react";
interface loaderProps{
  color?:string ;
}
const Loader: React.FC<loaderProps> = (props: loaderProps) => {
  const {color="#8b6b10"}=props;
  return (
    <svg
      width="80px"
      height="60px"
