export default Loader;
import React, { FC, Fragment } from "react";
import { FileItemLocalizerSelector } from "../../../../localization";
import { LocalLabels } from "../../../../localization/localization";
import Loader from "../../../../utils/loader";
import {
  CheckCircle,
  DoDisturb,
  UploadDone,
  UploadError,
} from "../../../icons";
import { FileItemStatusProps } from "./FileItemStatusProps";

const FileItemStatus: FC<FileItemStatusProps> = (
  props: FileItemStatusProps,
) => {
  const {
    valid,
    uploadStatus,
    //message,
    localization,
  } = props;
  const FileItemStatusLocalizer: LocalLabels = FileItemLocalizerSelector(
    localization,
  ).status as LocalLabels;
  return (
    <Fragment>
      {uploadStatus ? (
        uploadStatus === "uploading" ? (
          <div className="file-status-loading">
            <Loader />
            <p>
              {FileItemStatusLocalizer.uploading as string}
              {/* localization === "ES-es" ? "Subiendo" : "uploading" */}
            </p>
          </div>
        ) : uploadStatus === "success" ? (
          <div className="file-status-ok upload">
            <UploadDone
              color="#4caf50"
              size="small"
              //style={styles.icons}
              className="status-icon"
            />
            {FileItemStatusLocalizer.success as string}
            {/* message || localization === "ES-es" ? "éxito" : "success" */}
          </div>
        ) : (
          <div className="file-status-error upload">
            <UploadError
              color="#f44336"
              size="semi-medium"
              className="status-icon"
            />
            {FileItemStatusLocalizer.error as string}
          </div>
        )
      ) : valid ? (
        <div className="file-status-ok">
          <CheckCircle
            color="#4caf50"
            size="small"
            //style={styles.icons}
            className="status-icon"
          />
          {FileItemStatusLocalizer.valid as string}
          {/* localization === "ES-es" ? "válido" : "valid" */}
        </div>
      ) : (
        <div className="file-status-error">
          <DoDisturb
            color="#f44336"
            size="small"
            className="status-icon"
            //style={styles.icons}
          />
          {FileItemStatusLocalizer.denied as string}
          {/* localization === "ES-es" ? "No válido" : "Denied" */}
        </div>
      )}
    </Fragment>
  );
};
export default FileItemStatus;
import React, { FC } from "react";
import { FileItemLocalizerSelector } from "../../../../localization";
import {
  Localization,
  LocalLabels,
} from "../../../../localization/localization";
import { Cancel } from "../../../icons";

import FileItemStatus from "../FileItemStatus/FileItemStatus";

const FileItemFullInfoLayer: FC<FileItemFullInfoLayerProps> = (
  props: FileItemFullInfoLayerProps,
) => {
  const {
    showInfo,
    valid,
    onClose,
    fileName,
    fileSize,
    fileType,
    uploadStatus,
    uploadMessage,
    localization,
    errors,
    resultOnTooltip,
  } = props;
  const FileItemFullInfoLocalizer: LocalLabels = FileItemLocalizerSelector(
    localization as Localization,
  ).fullInfoLayer as LocalLabels;
  const handleCloseInfo = () => {
    onClose?.();
  };
  return (
    <div className={showInfo ? "full-info" : "full-info hide"}>
      <span
        style={{
          display: "flex",
          flexWrap: "wrap",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Cancel
          style={{ margin: 0, right: 0, top: 0 }}
          color="rgba(255,255,255,0.8)"
          onClick={handleCloseInfo}
          colorFill="black"
        />
        {uploadStatus && uploadStatus !== "uploading" ? (
          <div className={"file-status"}>
            <FileItemStatus
              uploadStatus={uploadStatus}
              localization={localization as Localization}
            />
          </div>
        ) : (
          <div className="file-status">
            <FileItemStatus
              valid={valid}
              localization={localization as Localization}
            />
          </div>
        )}
      </span>
      
      {!resultOnTooltip && !uploadMessage && errors && errors.length > 0 && (
        <div className={`name error`}>
          {errors.map((error, index) => (
            <div key={index + 1}>{`- ${error}.\n`}</div>
          ))}
        </div>
      )}
      {!resultOnTooltip && uploadMessage && (
        <div className={`name ${uploadStatus}`}>{uploadMessage}</div>
      )}
      <div className="name">
        <span className="sub-label">
          {FileItemFullInfoLocalizer.name as string}
          {/* localization === "ES-es" ? "Nombre: " : "Name: " */}
        </span>
      </div>
      <div className="name">{fileName}</div>
      {/** FILE SIZE */}
      <div className="size">
        <span className="sub-label">
          {FileItemFullInfoLocalizer.size as string}
        </span>
      </div>
      <div className="name">{fileSize}</div>
      {/** FILE TYPE */}
      <div className="mime">
        <span className="sub-label">
          {FileItemFullInfoLocalizer.type as string}
        </span>
      </div>
      <div className="mime">{fileType}</div>
    </div>
  );
};
export default FileItemFullInfoLayer;

export interface FileItemFullInfoLayerProps {
  showInfo: boolean;
  fileName: string;
  fileSize: string;
