import React, { FC, Fragment, useEffect, useState } from "react";
//import { FileItemProps } from "../FileItem/FileItemProps";
import FileItemStatus from "../FileItemStatus/FileItemStatus";
import { PlayIcon,Cancel, Visibility, Info } from "../../../icons";
import { Localization } from "../../../../localization/localization";
//import {shrinkWord} from "./../../utils";
export interface FileItemMainLayerProps {
  showInfo: boolean;
  onOpenInfo: Function;
  onOpenImage: Function | undefined;
  onOpenVideo: Function | undefined;
  onDelete: Function | undefined;
  //fileNamePosition: FileItemProps["fileName"];
  fileName: string;
  info: boolean;
  valid: boolean;
  isImage: boolean;
  isVideo: boolean;
  uploadStatus?: undefined | "uploading" | "success" | "error";
  sizeFormatted: string;
  /**
   * This feature is hidden, it is not present on documentation
   * because is experimental. If you found this prop, you can test it
   * and comment us if any issue is found. Thanks in advance.
   *
   * Make file name, info layer, size and "valid message"
   * not visible
   */
  onlyImage?: boolean;
  /**
   * language to be used
   * for now
   * only English and Spanish is supported
   */
  localization?: Localization;
  hovering?: boolean;
}

const FileItemMainLayer: FC<FileItemMainLayerProps> = (
  props: FileItemMainLayerProps
) => {
  const {
    showInfo,
    onDelete,
    info,
    valid,
    isImage,
    isVideo,
    onlyImage,
    onOpenInfo,
    onOpenImage,
    onOpenVideo,
    sizeFormatted,
    uploadStatus,
    localization,
    hovering,
  } = props;
  const handleDelete = () => {
    onDelete?.();
  };
  const handleOpenInfo = () => {
    onOpenInfo?.();
  };
  const handleOpenImage = () => {
    onOpenImage?.();
  };
  const handleOpenVideo =()=>{
    onOpenVideo?.();
  }
  const [uploadComplete, setUploadComplete] = useState<boolean>(false);
  useEffect(() => {
    if (uploadStatus === "success") {
      setTimeout(() => {
        setUploadComplete(true);
      }, 2000);
    }
  }, [uploadStatus]);
  return (
    <Fragment>
      {hovering ? (
        <div className="info-container">
          <div
            className={
              uploadStatus === "uploading" || !onDelete
                ? "status-close uploading"
                : showInfo
                ? "status-close hide"
                : "status-close"
            }
          >
            <Cancel
              color="rgba(255,255,255,0.8)"
              onClick={uploadStatus === "uploading" ? undefined : handleDelete}
              colorFill="black"
            />
          </div>

          {uploadStatus && !showInfo && (
            <div
              className={uploadComplete ? "file-status hide" : "file-status"}
            >
              <FileItemStatus
                uploadStatus={uploadStatus}
                localization={localization as Localization}
              />
            </div>
          )}

          <div className={"file-item-footer"}>
            {!onlyImage && uploadStatus && uploadComplete ? (
              <div className={showInfo ? "file-status hide" : "file-status"}>
                <div className="file-status-ok">
                  <FileItemStatus
                    uploadStatus={uploadStatus}
                    //message={localization==="ES-es"?"subido":"uploaded"}
                    localization={localization as Localization}
                  />
                </div>
              </div>
            ) : (
              <div className={showInfo ? "file-status hide" : "file-status"}>
                <FileItemStatus
                  valid={valid}
                  localization={localization as Localization}
                />
              </div>
            )}

            <div
              className={showInfo ? "size-open-info hide" : "size-open-info"}
            >
              {!onlyImage && (
                <div className={"file-item-size"}>{sizeFormatted}</div>
              )}

              {isImage && onOpenImage && valid && (
                <Visibility
                  className="view-in-image-file-item"
                  color="rgba(255,255,255,0.9)"
                  onClick={handleOpenImage}
                  size="small"
                />
              )}
              {isVideo && onOpenVideo && valid && (
                <PlayIcon
                  className="view-in-image-file-item"
                  color="rgba(255,255,255,0.9)"
                  onClick={handleOpenVideo}
                  size="small"
                />
              )}
              {!onlyImage && info && (
                <Info
                  color="rgba(255,255,255,0.8)"
                  onClick={handleOpenInfo}
                  colorFill="black"
                />
              )}
            </div>
          </div>
        </div>
      ) : (
        <Fragment>
          <div className="info-container">
            <div
              className={
                uploadStatus === "uploading" || !onDelete
                  ? "status-close uploading"
                  : showInfo
                  ? "status-close hide"
                  : "status-close"
              }
            >
              {/* <Cancel
              color="rgba(255,255,255,0.8)"
              onClick={uploadStatus === "uploading" ? undefined : handleDelete}
              colorFill="black"
            /> */}
            </div>

            {uploadStatus && !showInfo && (
              <div
                className={uploadComplete ? "file-status hide" : "file-status"}
              >
                <FileItemStatus
                  uploadStatus={uploadStatus}
                  localization={localization as Localization}
                />
              </div>
            )}

            <div className={"file-item-footer"}>
              {!onlyImage && uploadStatus && uploadComplete ? (
                <div className={showInfo ? "file-status hide" : "file-status"}>
                  <div
                    className="file-status-ok"
                    style={{ marginBottom: "1px" }}
                  >
                    <FileItemStatus
                      uploadStatus={uploadStatus}
                      //message={localization==="ES-es"?"subido":"uploaded"}
                      localization={localization as Localization}
                    />
                  </div>
                </div>
              ) : (
                <div
                  className={showInfo ? "file-status hide" : "file-status"}
                  style={{ marginBottom: "1px" }}
                >
                  <FileItemStatus
                    valid={valid}
                    localization={localization as Localization}
                  />
                </div>
              )}

              <div
                className={showInfo ? "size-open-info hide" : "size-open-info"}
              ></div>
            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};
export default FileItemMainLayer;
import React, { FC, Fragment, useEffect, useState } from "react";
import { TooltipProps } from "./TooltipProps";
import "./Tooltip.scss";
import { UPLOADSTATUS } from "../../../components/dropzone/components/utils/validation.utils";
const Tooltip: FC<TooltipProps> = (props: TooltipProps) => {
  const {
    //message,
    //style,
    //children,
    //color,
    uploadStatus,
    valid,
    errors,
    //className,
    uploadMessage,
    open,
  } = props;
  const [statusClassName, setSatusClassName] = useState<string | undefined>(
    undefined,
  );
  const [message, setMessage] = useState<undefined | string>(undefined);
  const handleChangeStatus = (uploadStatus?: UPLOADSTATUS, valid?: boolean) => {
    if (valid) {
      //if file is valid, then it can be uploaded and can check
      // upload status
      //setSattusClassName("success");
      if (!uploadStatus) {
        //when uploadStatus is undefined, File have not been uploaded yet
        //setSattusClassName(undefined);
        setMessage(undefined);
      } else {
        //otherwise is only needed to check error or success
        setMessage(uploadMessage);
        if (uploadStatus === "success") {
          setSatusClassName("success");
        } else if (uploadStatus === "error") {
          setSatusClassName("not-valid-error");
        } else {
          setSatusClassName(undefined);
        }
      }
    } else {
      // not valid has errors
      setSatusClassName("not-valid-error");
      setMessage(
        errors
          ? errors.reduce((acum: string, curr: string) => {
              acum += `${curr}. `;
              return acum;
            }, "")
          : "",
      );
    }
  };
  useEffect(() => {
    handleChangeStatus(uploadStatus, valid);
    // eslint-disable-next-line
  }, [uploadStatus, valid]);
  return (
    <Fragment>
      {open && message && statusClassName && (
        <span className={`dropzone-ui-tooltiptext ${statusClassName}`}>
          {
            /* !valid
              ? errors?.map((error, index) => (
                  <div key={index + 1}>{`- ${error}.\n`}</div>
                ))
              :  */ message
          }
        </span>
      )}
    </Fragment>
  );
};
export default Tooltip;
import React, { FC, Fragment, useEffect, useState } from "react";

import { FileItemProps, FileItemPropsDefault } from "./FileItemProps";
import "./FileItem.scss";
import { Paper } from "../../../paper";
import { mergeProps } from "@unlimited-react-components/kernel";
import {
  fileSizeFormater,
  getURLFileIco,
  readImagePromise,
  resizeImage,
  shrinkWord,
} from "../../utils";

import FileItemFullInfoLayer from "../FileItemFullInfoLayer/FileItemFullInfoLayer";
import FileItemImage from "../FileItemImage/FileItemImage";
import FileItemMainLayer from "../FileItemMainLayer/FileItemMainLayer";
import Tooltip from "../../../tooltip/components/Tooltip";

const FileItem: FC<FileItemProps> = (props: FileItemProps) => {
  const {
    file,
    onDelete,
    onSee,
    onWatch,
    style,
    preview,
    onlyImage,
    info,
    id,
    valid,
    uploadStatus,
    uploadMessage,
    hd,
    localization,
    errors,
    imageUrl,
    elevation,
    alwaysActive,
    resultOnTooltip,
  } = mergeProps(props, FileItemPropsDefault);
  //actionOnHover
  const [hovering, setHOvering] = useState<boolean>(false);
  const handleOnHoverEnter = () => {
    //if (!alwaysActive) {
    setHOvering(true);
    //}
  };
  const handleOnHoverLeave = () => {
    //if (!alwaysActive) {
    setHOvering(false);
    //}
  };
  /*   useEffect(() => {
    setHOvering(alwaysActive || false);
  }, [alwaysActive]); */

  //size
  const sizeFormatted: string = file ? fileSizeFormater(file.size) : "0 KB";

  const [isImage, setIsImage] = useState<boolean>(false);
  const [isVideo, setIsVideo] = useState<boolean>(false);
  const [url, setUrl] = useState<string>("");
  const [imageSource, setImageSource] = useState<string | undefined>(undefined);
  //alwaysActive
  const [showInfo, setShowInfo] = useState<boolean>(false);
  useEffect(() => {
    init(file, valid || false, preview || false, imageUrl);

    return () => {
      setImageSource(undefined);
      setIsImage(false);
      setIsVideo(false);
    };
  }, [file, valid, preview, imageUrl]);

  const init = async (
    file: File | undefined,
    valid: boolean,
    preview: boolean,
    imageUrl: string | undefined
  ) => {
    //////////////////////////////
    if (!file) return;
    const { url } = getURLFileIco(file as File);

    setUrl(url);

    if (imageUrl) {
      setIsImage(true);
      setImageSource(imageUrl);
      return;
    } else {
      const headerMime = file.type ? file.type.split("/")[0] : "octet";
      const tailMime = file.type ? file.type.split("/")[1] : "octet";
      setIsImage(headerMime === "image");
      setIsVideo(headerMime === "video" && ["mp4","ogg","webm"].includes(tailMime));
      if (preview && valid && headerMime === "image") {
        const response = await readImagePromise(file);
        if (response) {
          const cutt = await resizeImage(response);

          setImageSource(cutt as string);
        } else {
          setImageSource(undefined);
        }
      }
    }
    //////////////////////////////
  };

  const handleDelete = (): void => {
    if (onDelete) {
      onDelete(id);
    }
  };
  const handleOpenInfo = () => {
    setShowInfo(true);
  };
  const handleCloseInfo = () => {
    setShowInfo(true);

    setShowInfo(false);
  };
  const handleOpenVideo = async () => {
    if (file) {
      onWatch?.(file);
    }
  };
  const handleOpenImage = async () => {
    if (imageSource && file) {
      if (hd) {
        const response = await readImagePromise(file);
        onSee?.(response || "");
      } else {
        onSee?.(imageSource);
      }
    }
  };
  function handleClick<T extends HTMLDivElement>(
    e: React.MouseEvent<T, MouseEvent>
  ): void {
    //avoid children to trigger onClick ripple from parent
    e.stopPropagation();
  }
  if (file && typeof file.name == "string") {
    return (
      <div
        className={`dz-ui-file-item-container${
          resultOnTooltip ? " dz-ui-tooltip" : ""
        }`}
        onClick={handleClick}
        onMouseEnter={handleOnHoverEnter}
        onMouseLeave={handleOnHoverLeave}
      >
        <div className={`file-item-full-container-container`} style={style}>
          <Paper
            shadow={elevation}
            className={`file-item-icon-container ${showInfo ? " hide" : ""}`}
          >
            <FileItemImage
              imageSource={imageSource}
              url={url}
              fileName={file.name}
            />
            <FileItemMainLayer
              showInfo={showInfo}
              //fileNamePosition={fileName}
              fileName={file.name}
              onDelete={onDelete ? handleDelete : undefined}
              onOpenImage={onSee && preview ? handleOpenImage : undefined}
              onOpenVideo={onWatch && preview ? handleOpenVideo : undefined}
              isVideo={isVideo}
              onOpenInfo={handleOpenInfo}
              info={info || false}
              valid={valid || false}
              isImage={isImage}
              sizeFormatted={sizeFormatted}
              //fileNamePosition={undefined}
              uploadStatus={uploadStatus}
              localization={localization}
              onlyImage={onlyImage}
              hovering={alwaysActive || hovering}
            />
            <FileItemFullInfoLayer
              showInfo={showInfo}
              errors={errors}
              fileName={file.name}
              fileSize={fileSizeFormater(file.size)}
              fileType={file.type}
              valid={valid || false}
              onClose={handleCloseInfo}
              uploadStatus={uploadStatus}
              uploadMessage={uploadMessage}
              localization={localization}
              resultOnTooltip={resultOnTooltip}
            />
          </Paper>

          {!onlyImage && (
            <div className="file-item-name">{shrinkWord(file.name)}</div>
          )}
        </div>
        {resultOnTooltip && (
          <Tooltip
            //open={resultOnTooltip && hovering}
            open={true}
            uploadStatus={uploadStatus}
            valid={valid}
            errors={errors}
            uploadMessage={uploadMessage}
          ></Tooltip>
        )}
      </div>
    );
  } else return <Fragment></Fragment>;
};
export default FileItem;

/**
 * {fileName === "bottom" && (
      <div className="file-item-name">{shrinkWord(file.name)}</div>
    )}
 */
import { Cancel } from "../icons";
import "./FullScreenPreview.scss";
import React, { FC } from "react";
import { FullScreenPreviewProps } from "./FullScreenPreviewProps";

const FullScreenPreview: FC<FullScreenPreviewProps> = (
  props: FullScreenPreviewProps,
) => {
  const { imgSource, openImage, onClose } = props;

  function handleClose<T extends HTMLDivElement>(
    e: React.MouseEvent<T, MouseEvent>,
  ): void {
    //avoid children to trigger onClick ripple from parent
    e.stopPropagation();
    onClose?.();
  }
  return (
