import { MaterialButtonDefaultProps, MaterialButtonProps } from "@unlimited-react-components/material-button";
import { Localization } from "../../localization/localization";
import { CustomValidateFileResponse } from "../dropzone/components/utils/validation.utils";

export interface InputButtonProps extends MaterialButtonProps {
    /**
     * the id of the component
     */
    id?: string;
    /**
     * whether to accept multiple files or not
     */
    multiple?: boolean;
    /**
     * The list of mimetypes and extensions of files 
     * to accept. This is a comma separated list of
     *  mime types or file extensions. 
     * Eg.: image/\*,application/pdf,.psd
     */
    accept?: string;
    /**
     * If given, this label will be shwed inside the button
     */
    label?: string;
    /**
     * what to do after selecting the files. Returns the File[] to the parent component
     */
    onChange?: Function;
    /**
   * custom validator
   * must be a function that recieves as first parameter a File Object
   * and must return a boolean value
   */
    validator?: (f: File) => CustomValidateFileResponse;
    /**
     * Max number of files to be accepted.
     */
    //maxFiles?: number;
    /**
     * max file size allowed in bytes
     */
    maxFileSize?: number;
    /**
  * language to be used
  * for now
  * only English, French , Portuguese and Spanish are supported
  */
    localization?: Localization;
}

/**
 * The default props when not given
 */
export const InputButtonDefaultProps: InputButtonProps = {
    /**
     * default material button props
     */
    ...MaterialButtonDefaultProps,
    /**
     * default InputButton props
     */
    ...{
        multiple: false,
        accept: "",
        label: "Browse ...",
        onChange: () => { },
        //maxFiles: 7, 
        localization: "EN-en"

    }
}
import React, { Fragment, useRef } from "react";
import { MaterialButton } from "@unlimited-react-components/material-button";
import { mergeProps } from "@unlimited-react-components/kernel";
import { InputButtonDefaultProps, InputButtonProps } from "./InputButtonProps";
import {
  customValidateFile,
  FileValidated,
  FileValidator,
  validateFile,
} from "../dropzone/components/utils/validation.utils";
import { LocalLabels } from "../../localization/localization";
import { ValidateErrorLocalizerSelector } from "../../localization";

const InputButton: React.FC<InputButtonProps> = (props: InputButtonProps) => {
  let {
    accept,
    color,
    disabled,
    id,
    label,
    maxFileSize,
    multiple,
    onChange,
    style,
    textColor,
    validator,
    variant,localization
  } = mergeProps(props, InputButtonDefaultProps);
  const inputRef = useRef<HTMLInputElement>(null);
  const localValidator: FileValidator = {
    //It is assumed that input tag already validated accept property
    //accept: accept,
    maxFileSize: maxFileSize,
  };
  const ValidationErrorLocalizer: LocalLabels =
  ValidateErrorLocalizerSelector(localization);
  const handleOnChange: React.ChangeEventHandler<HTMLInputElement> = (
    evt: React.ChangeEvent<HTMLInputElement>,
  ): void => {
    let files: FileList = evt.target.files as FileList;
    const output: FileValidated[] = [];

    if (files && files.length > 0) {
      for (let i = 0, f: File; (f = files[i]); i++) {
        let validatedFile: FileValidated = validator
          ? customValidateFile(f, validator)
          : validateFile(f, localValidator, ValidationErrorLocalizer);
        output.push(validatedFile);
      }
    }
    let element: HTMLInputElement | null = inputRef.current;
    if (element) {
      element.value = "";
    }
    onChange?.(output);
  };
  function clickInput(e: React.MouseEvent<MouseEvent>): void {
    e.stopPropagation();
    e.preventDefault();
    let referenceInput = inputRef.current;
    if (referenceInput) {
      referenceInput.click();
    }
  }
  return (
    <Fragment>
      <MaterialButton
        style={style}
        color={color}
        variant={variant}
        //size={size}
        onClick={clickInput}
        disabled={disabled}
        textColor={textColor}
      >
        {label}
      </MaterialButton>
      <input
        id={id || ""}
        style={{ display: "none" }}
        ref={inputRef}
        onChange={handleOnChange}
        type="file"
        multiple={multiple}
        accept={accept}
      />
    </Fragment>
  );
};

export default InputButton;
