const clientId = `${process.env.CLIENT_ID}`;
const authority = `${process.env.TENANT_ID}`;
const redirectUri = `${process.env.REDIRECT_URI}`;

export const msalConfig = {
    auth: {
      clientId: clientId, //"c6794e02-3287-4053-979a-5c45704db40b",
      authority: authority, // This is a URL (e.g. https://login.microsoftonline.com/{your tenant ID}) 
      redirectUri: redirectUri,
    },
    cache: {
      cacheLocation: "sessionStorage", // This configures where your cache will be stored
      storeAuthStateInCookie: false, // Set this to "true" if you are having issues on IE11 or Edge
    }
  };

  // Add scopes here for ID token to be used at Microsoft identity platform endpoints.
  export const loginRequest = {
   scopes: ["User.Read"]
  };

  // Add the endpoints here for Microsoft Graph API services you'd like to use.
  export const graphConfig = {
      graphMeEndpoint: "https://graph.microsoft.com/v1.0"
  };
