// ./src/App.tsx

import "./App.css";
import React, {useState} from 'react';
import Path from 'path';
import uploadFileToBlob, { isStorageConfigured } from './azure-storage-blob';
import { Dropzone, FileItem } from "@dropzone-ui/react";

// import React from "react";
import { PageLayout } from "./components/PageLayout";
// Addition
import 'bootstrap/dist/css/bootstrap.css';
import { AuthenticatedTemplate, UnauthenticatedTemplate, useMsal } from "@azure/msal-react";
import { loginRequest } from "./authConfig";
import Button from "react-bootstrap/Button";
import { ProfileData } from "./components/ProfileData";
import { callMsGraph } from "./graph";
// End addition



function ProfileContent() {
  const { instance, accounts } = useMsal();
  const [graphData, setGraphData] = useState(null);

  const name = accounts[0] && accounts[0].name;

  function RequestProfileData() {
    const request = {
      ...loginRequest,
      account: accounts[0]
    };

    // Silently acquires an access token which is then attached to a request for Microsoft Graph data
    instance.acquireTokenSilent(request).then((response) => {
      callMsGraph(response.accessToken).then(response => setGraphData(response));
    }).catch((e) => {
      instance.acquireTokenPopup(request).then((response) => {
        callMsGraph(response.accessToken).then(response => setGraphData(response));
      });
    });
  }

  return (
    <>
      <h5 className="card-title">Welcome {name}</h5>
      {graphData ?
        <ProfileData graphData={graphData} />
        :
        <Button variant="secondary" onClick={RequestProfileData}>Request Profile Information</Button>
      }
    </>
  );
};

// Previous storage code
const storageConfigured = isStorageConfigured();




const App = (): JSX.Element => {
  // all blobs in container
  const [blobList, setBlobList] = useState<string[]>([]);

  // current file to upload into container
  const [fileSelected, setFileSelected] = useState(null);

  

  // UI/form management
  const [uploading, setUploading] = useState(false);
  const [inputKey, setInputKey] = useState(Math.random().toString(36));

  const onFileChange = (event: any) => {
    // capture file into state
    setFileSelected(event.target.files[0]);
  };

  const onFileUpload = async () => {
    // prepare UI
    setUploading(true);

    // *** UPLOAD TO AZURE STORAGE ***
    const blobsInContainer: string[] = await uploadFileToBlob(fileSelected);

    // prepare UI for results
    setBlobList(blobsInContainer);

    // reset state/form
    setFileSelected(null);
    setUploading(false);
    setInputKey(Math.random().toString(36));
  };

  // display form
  if (fileSelected !== null) console.log(fileSelected["name"])
  const DisplayForm = () => (
    <form>
      <input type="file" onChange={onFileChange} key={inputKey || ''} />
      <p>{fileSelected ? fileSelected["name"] : "Drag your files here or click in this area."}</p>
      <button type="submit" onClick={onFileUpload}>Upload</button>
    </form>

    
  )

  // display file name and image
  const DisplayImagesFromContainer = () => (
    <div>
      <h2>Container items</h2>
      <ul>
        {blobList.map((item) => {
          return (
            <li key={item}>
              <div>
                {Path.basename(item)}
                <br />
                <img src={item} alt={item} height="200" />
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );

  //End previous storage code

  const [files, setFiles] = useState<any[]>([]);
  const updateFiles = (incommingFiles: any) => {
    setFiles(incommingFiles);
  };
  
  const removeFile = (id: any) => {
    setFiles(files.filter((x) => x.id !== id));
  };

  // function App() {
  return (
    <PageLayout>
      <Dropzone onChange={updateFiles} value={files}>
      {files.length > 0 &&
        files.map((file) => (
          <FileItem 
            {...file} 
            onDelete={removeFile} 
            key={file.id} info />
        ))}
    </Dropzone>
      <div className="upload-area">
        {DisplayForm()}
      </div>
      <AuthenticatedTemplate>
        <ProfileContent />
        <div>
          <h1>Upload file to Azure Blob Storage</h1>
          {storageConfigured && !uploading && DisplayForm()}
          {storageConfigured && uploading && <div>Uploading</div>}
          <hr />
          {storageConfigured && blobList.length > 0 && DisplayImagesFromContainer()}
          {!storageConfigured && <div>Storage is not configured.</div>}
        </div>

      </AuthenticatedTemplate>

      <UnauthenticatedTemplate>
      </UnauthenticatedTemplate>
    </PageLayout>
  );
  // }
};

export default App;
